import XHRInterface, { XHROptions } from '@ilovepdf/ilovepdf-js-core/utils/XHRInterface';
export default class XHRPromise implements XHRInterface {
    get<T>(url: string, options?: XHROptions): Promise<T>;
    post<T>(url: string, data?: any, options?: XHROptions): Promise<T>;
    put<T>(url: string, data?: any, options?: XHROptions): Promise<T>;
    delete<T>(url: string, options?: XHROptions): Promise<T>;
    private static makeRequest;
    private static setHeaders;
    private static setEncoding;
    private static isBinary;
}
